$(document).ready(function () {
    /*Start slider function for the #slider*/
    'use strict';

    var slider = $("#typesSlider").Jslider({
        interval: 3000,
        showControls: false,
        showBullets: false
    });

    $(document).on('click', 'li', function () {

        var Jslider = slider.data('Jslider');

        switch ($(this).attr('id')) {
        case "visual":
            Jslider.goTo(1);
            break;
        case "audi":
            Jslider.goTo(2);
            break;
        case "kine":
            Jslider.goTo(3);
            break;
        default:
            Jslider.goTo(1);
        }

    });

});










