$.validator.addMethod("valueNotEquals", function (e, a, t) {
	return t != e;
}, "Value must not equal arg.");

$("#sessionForm").validate({
	rules: {
		level: {
			valueNotEquals: "Selecciona el nivel"
		},
		title: {
			required: true,
			minlength: 6
		},
		tags: {
			required: true,
			minlength: 6
		},
		description: {
			required: true,
			minlength: 30
		}
	},
	messages: {
		level: {
			valueNotEquals: "<div class='red' style='z-index: 9990;'><i class='fa fa-arrow-circle-o-up'></i> Selecciona el nivel porfavor</div>"
		},
		title: {
			required: "<div class='red' style='z-index: 9991;'><i class='fa fa-text-width'></i> Debe ingresar un titulo.</div>",
			minlength: "<div class='red' style='z-index: 9991;'><i class='fa fa-text-width'></i> El titulo debe tener un minimo de 6 caracteres.</div>"
		},
		tags: {
			required: "<div class='red' style='z-index: 9988;'><i class='fa fa-tags'></i> Debe ingresar almenos un tag.</div>",
			minlength: "<div class='red' style='z-index: 9988;'><i class='fa fa-tags'></i> El tag debe tener un minimo de 6 caracteres.</div>"
		},
		description: {
			required: "<div class='red' style='z-index: 9989;'><i class='fa fa-file-text'></i> Debe ingresar una descripción.</div>",
			minlength: "<div class='red' style='z-index: 9989;'><i class='fa fa-file-text'></i> Debe ingresar minimo 30 caracterés.</div>"
		}
	}
});
